"use strict";(self.webpackChunkmy_website=self.webpackChunkmy_website||[]).push([[450],{6029:n=>{n.exports=JSON.parse('{"blogPosts":[{"id":"design-system-intro","metadata":{"permalink":"/design-system-intro","editUrl":"https://github.com/facebook/docusaurus/tree/main/packages/create-docusaurus/templates/shared/blog/2022-12-05-design-system-intro/index.md","source":"@site/blog/2022-12-05-design-system-intro/index.md","title":"\ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc744 \uc815\uc758\ud560 \uc218 \uc788\ub294 \uac1c\ubc1c\uc790","description":"\ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc5d0 \ub300\ud55c \uc774\ud574\ub97c \ub192\uc774\uace0\uc790\ud558\ub294 \uae00\uc785\ub2c8\ub2e4.","date":"2022-12-05T00:00:00.000Z","formattedDate":"December 5, 2022","tags":[{"label":"design-system","permalink":"/tags/design-system"}],"readingTime":10.17,"hasTruncateMarker":true,"authors":[{"name":"Jiho Shon","title":"Frontend Developer in miso.inc","url":"https://github.com/peanut-lover","imageURL":"https://avatars.githubusercontent.com/u/20085849?v=4","key":"jiho"}],"frontMatter":{"title":"\ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc744 \uc815\uc758\ud560 \uc218 \uc788\ub294 \uac1c\ubc1c\uc790","description":"\ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc5d0 \ub300\ud55c \uc774\ud574\ub97c \ub192\uc774\uace0\uc790\ud558\ub294 \uae00\uc785\ub2c8\ub2e4.","slug":"design-system-intro","authors":"jiho","tags":["design-system"],"toc_min_heading_level":2,"toc_max_heading_level":5},"nextItem":{"title":"\ube14\ub85c\uadf8 \uc2dc\uc791\uc744 \uc54c\ub9ac\uba70...","permalink":"/welcome-docusaurus-v2"}},"content":"\ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc740 5\uac00\uc9c0\ub85c \uc815\uc758\ud560 \uc218 \uc788\uc744 \uac83 \uac19\uc2b5\ub2c8\ub2e4.\\n\\n1. \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc740 \uc77c\uad00\uc131\uc744 \uc9c0\ucf1c\uc918\uc11c \uc0ac\uc6a9\uc790\uc758 \uacbd\ud5d8\uc744 \uac1c\uc120\ud574\uc900\ub2e4.\\n2. \uc7ac\uc0ac\uc6a9\uc131\uc744 \uc62c\ub824\uc11c \uc77c\uc758 \uc0dd\uc0b0\uc131\uc744 \ub192\uc5ec\uc900\ub2e4.\\n3. \uc704 \ub450\uac00\uc9c0\ub97c \ud1b5\ud574 \ube44\uc9c0\ub2c8\uc2a4 \uc784\ud329\ud2b8\ub97c \uc904 \uc218 \uc788\ub2e4.\\n4. \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc740 \ud56d\uc0c1 \uc9c4\ud654\ud55c\ub2e4.\\n5. \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc740 \uc815\uc758\ud558\ub294 \uc0ac\ub78c\ub9c8\ub2e4 \uc815\uc758 \uc790\uccb4\uac00 \ub2e4\ub97c \uc218 \uc788\ub2e4.\\n\\n\uadf8\ub9ac\uace0 \uac1c\uc778\uc801\uc73c\ub85c \uc870\uc0ac\ub97c \ud558\uba74\uc11c \ub290\uaf08\uc9c0\ub9cc, \uac1c\ubc1c\uc790 \uc785\uc7a5\uc5d0\uc11c\ub294 \ub514\uc790\uc778\uc2dc\uc2a4\ud15c \ucef4\ud3ec\ub10c\ud2b8 \ub77c\uc774\ube0c\ub7ec\ub9ac\ub294 \uc0ac\uc6a9\uc790\uc5d0\uac8c \uc5b4\ub290\uc815\ub3c4 \ub178\ucd9c\uc2dc\ud0ac \uac83\uc778\uac00\uac00 \uc911\uc694\ud55c \uac83 \uac19\ub2e4. \ubc30\uc2dc\uc2dc(\ubc30\ubbfc\uc758 \ubc31\uc624\ud53c\uc2a4 \ub514\uc790\uc778\uc2dc\uc2a4\ud15c)\ub294 organism \uc218\uc900\uc758 \ucef4\ud3ec\ub10c\ud2b8\ub9cc \ub178\ucd9c\uc2dc\ucf1c\uc11c \uc0ac\uc6a9\uc790\ub4e4\uc758 \uc77c\uad00\uc801\uc778 \ubc31\uc624\ud53c\uc2a4 \uac1c\ubc1c\uc744 \uc720\ub3c4\ud558\ub294 \uac83 \uac19\uc558\ub2e4. (\uc6b4\uc601\uc758 \ud6a8\uc728\uc131\uc744 \ub192\uc774\uae30 \uc704\ud574)\\n\\n\ub108\ubb34 \uc704\ud0a4\ub9cc \ubcf4\uace0 \ub9cc\ub4e0 \ubd80\ubd84\uc774 \ub9ce\uc74c.\\n\\n\ub2e4\uc2dc \uc815\ub9ac\ud560 \ud544\uc694\uc131\uc774 \uc788\uc74c.\\n\\n> \uc65c\ud544\uc694\ud558\uace0 \uc5b8\uc81c \ub514\uc790\uc778\uc2dc\uc2a4\ud15c\uc744 \uc0ac\uc6a9\ud574\uc57c\uc9c0?\\n> https://story.pxd.co.kr/1434\\n>\\n> \uc6b0\ud615, \ubc30\uc2dc\uc2dc \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c \ucca0\ud559 \ubc0f \uac00\uc774\ub4dc\\n> https://www.youtube.com/watch?v=yR1g_A1-xFs\\n\\n\uc774\uac70 \ub450\uac1c \ud65c\uc6a9\ud574\ubcf4\uae30.\\n\\n## \ud83d\udc40 \uc694\uc0c8 \uc790\uc8fc \uc5b8\uae09\ub418\ub294 \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c?\\n\\n\uc720\uba85 \ucee8\ud37c\ub7f0\uc2a4, \uc720\ub2c8\ucf58 \uae30\uc5c5\uc774 \ub41c \uc2a4\ud0c0\ud2b8\uc5c5 \ub4f1\ub4f1\uc5d0 \\"\ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc744 \uc774\ub807\uac8c \ub3c4\uc785\ud574\uc11c \uc0dd\uc0b0\uc131\uc744 \ub192\uc600\ub2e4.\\"\uc640 \uac19\uc740 \uc774\uc57c\uae30\uac00 \ub098\uc624\uace4 \ud569\ub2c8\ub2e4.\\n\\n\uaddc\ubaa8\uac00 \uc791\uac70\ub098 \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc758 \ud544\uc694\uc131(\uc11c\ube44\uc2a4\uac00 \ud558\ub098\uc77c \uacbd\uc6b0)\uc744 \ubabb \ub290\ub07c\ub294 \uc791\uc740 \uc2a4\ud0c0\ud2b8\uc5c5\uc5d0 \ub2e4\ub2c8\ub294 \uac1c\ubc1c\uc790\ub4e4\uc740 \ub9e4\ubc88 \uae30\ub2a5\uc744 \uac1c\ubc1c\ud560 \ub54c \ub514\uc790\uc774\ub108\uac00 \uc900 \uc2dc\uc548\uc744 \ucc98\uc74c\ubd80\ud130 scratch\ud574\uc11c \ub9cc\ub4e4\uac70\ub098 \uc774 \ubc29\uc2dd\uc774 \ud53c\uace4\ud558\uac8c \ub290\uaef4\uc9c4\ub2e4\uba74 \uc791\uac8c \ucef4\ud3ec\ub10c\ud2b8\ud654\ub97c \uc9c4\ud589\ud574\uc11c \ud558\ub098\uc758 \ud504\ub85c\uc81d\ud2b8\uc5d0 \ud55c\uc815\ud574\uc11c \uc7ac\uc0ac\uc6a9\uc744 \ud558\uac8c\ub429\ub2c8\ub2e4.\\n\\n\uc774\ub7ec\ud55c \ubc29\uc2dd\uacfc \ucc28\uc774\uc810\uc740 \ubb34\uc5c7\uc774\uace0 \uc5b8\uc81c \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc744 \ub3c4\uc785\ud574\uc57c\ud560\uae4c\uc694?\\n\\n\uadf8\ub7fc \uc65c \ud544\uc694\ud55c \uc2dc\uc810\uc774 \uc62c\uae4c? \uadf8\ub9ac\uace0 \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc774 \uad6c\uccb4\uc801\uc73c\ub85c \ubb50\ub77c\uace0 \uc815\uc758\ud560 \uc218 \uc788\uc744\uae4c?\\n\\n\uadf8\ub9ac\uace0 \ucef4\ud3ec\ub10c\ud2b8 \uae30\ubc18 vs \ud30c\uc6b4\ub370\uc774\uc158 \uae30\ubc18 \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c \uc911 \uc5b4\ub5a4 \ubc29\uc2dd\uc774 \uc5b4\ub5a4 \uc0c1\ud669\uc5d0 \uc801\ud569\ud560\uae4c?\\n\\n\x3c!--truncate--\x3e\\n\\n## \u270d\ufe0f \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc5d0 \ub300\ud55c \uc815\uc758?\\n\\n\uc5ec\ub7ec \uc790\ub8cc, \ube14\ub85c\uadf8\ub4e4\uc744 \uc0b4\ud3b4\ubd24\uc744 \ub54c \ub2e4\uc591\ud55c \ud45c\ud604\uc774 \uc788\uc5c8\uc9c0\ub9cc \uac70\uc758 \ub9e5\ub77d\uc744 \uac19\uc558\uc2b5\ub2c8\ub2e4.\\n\\n- _\ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc740 \ub514\uc790\uc778 \uc6d0\uce59, \uaddc\uaca9, \ub2e4\uc2dc \uc0ac\uc6a9\ud560 \uc218 \uc788\ub294 UI \ud328\ud134\uacfc \ucef4\ud3ec\ub10c\ud2b8, \ucf54\ub4dc\ub97c \ud3ec\uad04\ud558\ub294 \uc885\ud569 \uc138\ud2b8\uc785\ub2c8\ub2e4_\\n\\n- _A Design system is a set of interconnected patterns and shared practices coherently organized_\\n\\n\ud558\uc9c0\ub9cc \uac01 \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\ub9c8\ub2e4 \ud3ec\uad04\ud558\ub294 \ubc94\uc704\ub294 \ub2ec\ub790\uc2b5\ub2c8\ub2e4. `pattern libraries`\uc758 \uc5ed\ud560\ub9cc\ud558\ub294 \uacbd\uc6b0\ub3c4 \uc874\uc7ac\ud558\uae30\ub3c4 \ud588\uc2b5\ub2c8\ub2e4.\\n\\n### \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc758 \uc5ed\ud560\\n\\n> Design systems aid in digital product design and development of products such as applications or websites\\n\\n\\"\uc5b4\ud50c\ub9ac\ucf00\uc774\uc158\uc774\ub098 \uc6f9\uc0ac\uc774\ud2b8 \uac19\uc740 product\uc758 \ub514\uc790\uc778\uacfc \uac1c\ubc1c\uc5d0 \ub3c4\uc6c0\uc744 \uc900\ub2e4\\" \ucd94\uc0c1\uc801\uc774\uc9c0\ub9cc \uba85\ud655\ud55c \ud45c\ud604\uc778 \uac83 \uac19\ub124\uc694.\\n\\n### \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc758 \uad6c\uc131 \ud83d\udd27\\n\\n![DesignSystem](./img/designSystemStructure.png)\\n\\n> They may contain, but are not limited to, pattern libraries, design languages, style guides, coded components, brand languages, and documentation.\\n>\\n> `pattern libraries`, `design language`, `style guides`, `coded components`, `brand languages`, and `documentation`\uc774\uc9c0\ub9cc \uc81c\ud55c\ub418\uc9c0\ub294 \uc54a\uc2b5\ub2c8\ub2e4. \uc774\ub7ec\ud55c \ucc38\uc870 \uc790\ub8cc\ub4e4\uc740 \ub2e4\ub978 \ud300\ub4e4(\ub514\uc790\uc774\ub108, \uac1c\ubc1c\uc790, PM\ub97c \ud3ec\ud568\ud574\uc11c)\uc774 Product\ub97c \ub514\uc790\uc778\ud558\uace0 \ube4c\ub4dc\ud558\ub294\ub370 \ub3c4\uc6c0\uc744 \uc8fc\ub294 \uc5ed\ud560\uc744 \ud569\ub2c8\ub2e4.\\n\\n\ub514\uc790\uc778 \uc2dc\uc2a4\ud15c \uad00\ub828\ud574\uc11c \ub9ac\uc11c\uce58\ub97c \ud574\ubcfc \ub54c \uc704\uc640 \uac19\uc774 \ub2e4\uc591\ud55c \ud0a4\uc6cc\ub4dc\ub4e4\uc774 \uc790\uc8fc \ub4f1\uc7a5\ud558\ub294\ub370 `coded components`\ub97c \uc81c\uc678\ud558\uace4 \uac1c\ubc1c\uc790 \uc785\uc7a5\uc5d0\uc11c\ub294 \ub2e4\uc18c \ub0af\uc120 \uc774\ub984\ub4e4\uc774 \uac00\ub4dd\ud569\ub2c8\ub2e4.\\n\\n\u2705 **\ud574\ub2f9 \ud0a4\uc6cc\ub4dc\ub4e4\uc5d0 \ub300\ud55c \uc815\ud655\ud55c \uc774\ud574\uac00 \ud544\uc694\ud574\ubcf4\uc785\ub2c8\ub2e4.**\\n\\npattern libraries, design language, style guides, coded components\\n\\n### \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc758 \uc7a5\uc810\\n\\n> https://velog.io/@korean-davinci/%EB%94%94%EC%9E%90%EC%9D%B8-%EC%8B%9C%EC%8A%A4%ED%85%9C%EC%9D%B4%EB%9E%80\\n\\n- \uc7ac\uc0ac\uc6a9\uc131\uc788\ub294 \ucef4\ud3ec\ub10c\ud2b8\ub4e4\uacfc \uacf5\uc720\ub418\ub294 \ub17c\ub9ac\uc801\uc778 \uadfc\uac70\ub97c \ud1b5\ud574 \ube60\ub978 Product \uac1c\ubc1c\uc744 \ub3c4\uc640\uc900\ub2e4.\\n- \uc77c\uad00\uc131\uc788\ub294 user expierence \uacfc design language\ub97c \ud1b5\ud574 \ub354 \ub098\uc740 Product\\n  - \uacf5\ub3d9\uc758 \ub514\uc790\uc778 \uc790\uc0b0 \ud655\ucda9 (\ud300\uc758 \uaddc\ubaa8\uac00 \ucee4\uc9c8\uc218\ub85d \uacf5\ub3d9 \uc9c0\uc2dd\uc744 \uc720\uc9c0\ud558\ub294 \uac8c \uc5b4\ub824\uc6cc\uc9d1\ub2c8\ub2e4.)\\n- technical, design debt \uac10\uc18c\ub97c \ud1b5\ud574 \uac1c\uc120\ub41c \uc720\uc9c0\ubcf4\uc218\uc640 \ud655\uc7a5\uc131\\n  - \ucee4\ubba4\ub2c8\ucf00\uc774\uc158 \uae30\uc900 \ub9c8\ub828\\n- product \ud300\uc774 user needs\ub97c \ud574\uacb0\ud558\ub294\ub370 \ub354\uc6b1 \uc9d1\uc911\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n### \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c \uae30\uc6d0\\n\\n> Mainstream interest about pattern languages for UI design surged again by the opening of Yahoo! Design Pattern Library in 2006[12] with the simultaneous introduction of Yahoo! User Interface Library (YUI Library for short). The simultaneous introduction was meant to allow more systematic design than mere components which the UI library has provided.\\n>\\n> \uc57c\ud6c4\uc758 \uc2dc\uc791\uacfc \ud568\uaed8 UI \ub514\uc790\uc778\uc744 \uc704\ud55c \ud328\ud134\uc774 \uc778\uae30\ub97c \uc5bb\uac8c\ub418\uc5c8\ub358 \uac83 \uac19\uc2b5\ub2c8\ub2e4. \uc774\ub7ec\ud55c \ub4f1\uc7a5\uc740 \ub2e8\uc21c\ud788 UI \ub77c\uc774\ube0c\ub7ec\ub9ac\uac00 \uc81c\uacf5\ud558\ub294 \ucef4\ud3ec\ub10c\ud2b8\ubcf4\ub2e4 \ub354\uc6b1 \uc2dc\uc2a4\ud15c\uc801\uc73c\ub85c \uad6c\uc131\ub41c \ub514\uc790\uc778\uc744 \uac00\ub2a5\ud558\uac8c \ud574\uc92c\uc2b5\ub2c8\ub2e4.\\n\\n\uc5ec\uae30\uc11c \uc8fc\ubaa9\ud560\ub9cc\ud55c \ub0b4\uc6a9\uc740 `more systematic design than mere components which the UI library has provided` \uc73c\ub85c \ubcfc \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ub2e8\uc21c\ud788 \uac1c\ubc1c\uc790\uac00 \uc624\ud508 \uc18c\uc2a4 UI \ub77c\uc774\ube0c\ub7ec\ub9ac\ub97c \uac00\uc838\uc640\uc11c \uc77c\uad00\ub418\uac8c \uc0ac\uc6a9\ud558\ub294 \uac83\uacfc \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc740 \ud655\uc2e4\ud788 \ub2e4\ub984\uc744 \ub9d0\ud558\ub294 \uac83\uc73c\ub85c \uc54c\ub824\uc9c4 \uac83 \uac19\uace0 \uadf8 \ucc28\uc774\ub97c \uc54c\uc544\uac00\ub294 \uac83\uc774 \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc5d0 \ub300\ud55c \ud559\uc2b5\uc774\ub77c\uace0 \uc0dd\uac01\uc774 \ub4ed\ub2c8\ub2e4.\\n\\n> Technical challenges of large-scale web projects led to the invention of systematic approaches in the 2010s, most notably BEM[13] and Atomic Design. The book about Atomic Design[14] helped popularize the term \\"Design System\\" since 2016.\\n\\n\uac70\ub300\ud55c \uaddc\ubaa8\uc758 \uc6f9 \ud504\ub85c\uc81d\ud2b8\ub4e4\uc744 \ub9cc\ub4dc\ub294 \uc5b4\ub824\uc6c0\ub4e4\uc774 \uc2dc\uc2a4\ud15c\uc801\uc778 \uc811\uadfc\uc744 \ubc1c\uba85\uc73c\ub85c \uc774\uc5b4\uc84c\uace0 Atomic Design \uc5d0 \ub300\ud55c \ucc45\uc774 Design System\uc758 \ub300\uc911\ud654\uc5d0 \ub3c4\uc6c0\uc774 \ub9ce\uc774 \ub418\uc5c8\ub2e4.\\n\\n\u2705 \uc704 \ub0b4\uc6a9\uc744 \ud655\uc778\ud574\ubcf4\ub2c8 \ub300\uaddc\ubaa8 \uc6f9 \ud504\ub85c\uc81d\ud2b8\ub97c \ud558\uba74\uc11c \uc0dd\uae30\ub294 \ub514\uc790\uc778 \uad00\ub828 \uc5b4\ub824\uc6c0\ub4e4\uc744 \ud655\uc778\ud574\ubcfc \ud544\uc694\uc131\uc774 \uc788\uc5b4\ubcf4\uc785\ub2c8\ub2e4.\\n\\n\u2705 Atomic Design \uc5d0 \ub300\ud55c \uc774\ud574\ub97c \ud558\uba74 \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\ub3c4 \uc790\uc5f0\uc2a4\ub808 \uc774\ud574\ud560 \uc218 \uc788\uc5b4\ubcf4\uc785\ub2c8\ub2e4.\\n\\n## (\ub17c\uc678) Difference between pattern languages and design systems and UI kits\\n\\n// \uc774 \ub0b4\uc6a9\uc740 \ube7c\ub3c4 \ub420\ub4ef\ud568.\\n\\n\ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uacfc \uc9c0\uae08\uae4c\uc9c0 \uc6b0\ub9ac\uac00 \ud574\uc654\ub358 \uac83\ub4e4\uc758 \uad6c\ubd84\uc744 \uc774\ud574\ud558\ub294\ub370 \ub3c4\uc6c0\uc774 \ub9ce\uc774 \ub418\ub294 \ub0b4\uc6a9\uc774\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc758 \uae30\uc6d0\uc744 \uc0b4\ud53c\ub2e4\ubcf4\uba74 `pattern language` \ub77c\ub294 \uc6a9\uc5b4\uac00 \uc790\uc8fc \ub098\uc624\ub294\ub370 \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc640 \ub3d9\uc77c\uc2dc\ub418\uc5b4 \uc0ac\uc6a9\ub418\ub294 \uac1c\ub150\ucc98\ub7fc \ubcf4\uc600\uc2b5\ub2c8\ub2e4. \ud558\uc9c0\ub9cc \uc5c4\uc5f0\ud788 \ub2e4\ub978 \uac1c\ub150\uc784\uc744 \uc5ec\uae30\uc11c \uc54c \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n> A pattern language allows its patterns to exist in many different shapes and forms \u2013 for example, a login form, with an input field for username, password, and buttons to log in, register and retrieve lost password is a pattern, no matter if the buttons are green or purple.\\n>\\n> pattern language\ub294 \ub2e4\ub978 \ud615\ud0dc\uc640 \ubaa8\uc591\uc774 \uc874\uc7ac\ud560 \uc218 \uc788\ub294 \ud328\ud134 \uadf8 \uc790\uccb4\ub97c \uc758\ubbf8\ud558\uac8c \ub429\ub2c8\ub2e4.\\n\\n\uc774 \uac1c\ub150\uc73c\ub85c \uc774\ud574\ud55c pattern language\ub294 \ucd5c\uadfc \ub9ce\uc774 \ub4e4\uc5c8\ub358 `headless UI`\uac00 pattern language\ub9cc\uc744 \uc81c\uacf5\ud558\ub294 \uac83 \uac19\uc558\uc2b5\ub2c8\ub2e4.\\n\\n> ## References\\n>\\n> \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc5d0 \ub300\ud558 \uac70\uc2dc\uc801\uc778 \uc774\ud574 & \ud544\uc694\uc131\\n> https://www.uxpin.com/studio/blog/design-systems-vs-pattern-libraries-vs-style-guides-whats-difference/\\n>\\n> \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc5d0\uc11c\uc758 \ud30c\uc6b4\ub370\uc774\uc158\uc774\ub780?\\n> https://danmall.com/posts/folly-of-design-system-foundations/\\n>\\n> \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c Linear \uc758 \ud55c\uacc4\ub97c \uc5b4\ub5bb\uac8c \uadf9\ubcf5\ud558\uace0 \uc788\uc9c0?\\n> [FECONF 2022 \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c, \ud615\ud0dc\ub97c \ub118\uc5b4\uc11c](https://www.youtube.com/watch?v=21eiJc90ggo)\\n>\\n> \uc65c\ud544\uc694\ud558\uace0 \uc5b8\uc81c \ub514\uc790\uc778\uc2dc\uc2a4\ud15c\uc744 \uc0ac\uc6a9\ud574\uc57c\uc9c0?\\n> https://story.pxd.co.kr/1434 > https://velog.io/@korean-davinci/\ub514\uc790\uc778-\uc2dc\uc2a4\ud15c\uc774\ub780\\n>\\n> \uc6b0\ud615, \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c \ucca0\ud559 \ubc0f \uac00\uc774\ub4dc\\n> https://www.youtube.com/watch?v=yR1g_A1-xFs\\n>\\n> \ub9ac\uc561\ud2b8 & \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c \uc2e4\uc2b5 \uac00\uc774\ub4dc\\n> https://velog.io/@velopert/design-system-using-typescript-and-storybook"},{"id":"welcome-docusaurus-v2","metadata":{"permalink":"/welcome-docusaurus-v2","editUrl":"https://github.com/facebook/docusaurus/tree/main/packages/create-docusaurus/templates/shared/blog/2022-12-04-intro-post.md","source":"@site/blog/2022-12-04-intro-post.md","title":"\ube14\ub85c\uadf8 \uc2dc\uc791\uc744 \uc54c\ub9ac\uba70...","description":"This is my first post on Docusaurus 2.","date":"2022-12-04T00:00:00.000Z","formattedDate":"December 4, 2022","tags":[{"label":"intro","permalink":"/tags/intro"},{"label":"docusaurus-v2","permalink":"/tags/docusaurus-v-2"}],"readingTime":2.305,"hasTruncateMarker":true,"authors":[{"name":"Jiho Shon","title":"Frontend Developer in miso.inc","url":"https://github.com/peanut-lover","imageURL":"https://avatars.githubusercontent.com/u/20085849?v=4","key":"jiho"}],"frontMatter":{"title":"\ube14\ub85c\uadf8 \uc2dc\uc791\uc744 \uc54c\ub9ac\uba70...","description":"This is my first post on Docusaurus 2.","slug":"welcome-docusaurus-v2","authors":"jiho","tags":["intro","docusaurus-v2"],"toc_min_heading_level":2,"toc_max_heading_level":5},"prevItem":{"title":"\ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc744 \uc815\uc758\ud560 \uc218 \uc788\ub294 \uac1c\ubc1c\uc790","permalink":"/design-system-intro"}},"content":"\uc548\ub155\ud558\uc138\uc694. \ube14\ub85c\uadf8 \uc8fc\uc778\uc785\ub2c8\ub2e4.\\n\\n\uc55e\uc73c\ub85c \uc804\ubb38\uc801\uc778 \ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc790\ub85c \uc131\uc7a5\ud558\uae30 \uc704\ud574\uc11c\\n\ub2e4\uc591\ud55c \uc88b\uc740 \uae00\ub4e4\uc744 \uc791\uc131\ud574\ubcf4\uaca0\uc2b5\ub2c8\ub2e4.\\n\\n\uc798\ubabb\ub41c \ub0b4\uc6a9 \ud639\uc740 \uc218\uc815\uc774 \ud544\uc694\ud55c \ub0b4\uc6a9\uc774 \uc788\ub2e4\uba74 \uc544\ub08c\uc5c6\uc774 \ud53c\ub4dc\ubc31\uc774 \uc8fc\uc2dc\uba74 \uac10\uc0ac\ud560 \uac83 \uac19\uc2b5\ub2c8\ub2e4.\\n\\n\uae00\uc4f0\ub294\ub370 \uc7ac\uc8fc\ub294 \uc5c6\uc9c0\ub9cc \ub9ce\uc774 \ud574\ubd10\uc57c \ub298\uac70\ub77c\uace0 \uc0dd\uac01\uc774 \ub4ed\ub2c8\ub2e4! \uac10\uc0ac\ud569\ub2c8\ub2e4. (\uafb8\ubc85)\\n\\n\x3c!--truncate--\x3e\\n\\n## 2022 4\ubd84\uae30 \uc791\uc131 \uc608\uc815 \uae00 \uacf5\uc9c0\\n\\n\uccab \ube14\ub85c\uadf8\uc758 \uc2dc\uc791\uc744 \uc54c\ub9ac\uba70 \uc2a4\uc2a4\ub85c \ud3ec\uc2a4\ud305 TODO \ub9ac\uc2a4\ud2b8\ub97c \ub0a8\uaca8\ub450\uace0\uc790 \ud569\ub2c8\ub2e4.\\n\\n\uac1c\uc778\uc801\uc73c\ub85c \uc774\ubc88 \ud574\ub294 \ud55c\ub2ec \ubc16\uc5d0 \ub0a8\uc9c0\uc54a\uc558\uc9c0\ub9cc \ubcf4\ub78c\ucc28\uac8c \ubcf4\ub0b4\uace0 \uc2f6\uc5b4\uc11c **Fast Campus Inner Circle(\ud504\ub860\ud2b8\uc5d4\ub4dc - \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\ud3b8)** \uc2a4\ud130\ub514(2022-12-03 ~ 2022-01-31)\ub97c \uc2dc\uc791\ud558\uac8c\ub418\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\uadf8\ub798\uc11c \ub2f9\ubd84\uac04 \ube14\ub85c\uadf8\uc5d0 \ub0a8\uae30\ub294 \uae00\ub4e4\uc740 `\ud504\ub860\ud2b8\uc5d4\ub4dc` & `\ub514\uc790\uc778 \uc2dc\uc2a4\ud15c` & `\ud6a8\uc728\uc131\uc788\ub294 \uac1c\ubc1c \ud504\ub85c\uc138\uc2a4`\uac00 \ud3ec\uc2a4\ud305 \ud575\uc2ec \ud0a4\uc6cc\ub4dc\ub4e4\uc774 \ub420 \uac83 \uac19\uc2b5\ub2c8\ub2e4.\\n\\n:::caution\\n\\n**\ud544\uc218**\ub85c \ud574\uc57c\ud558\ub294 \ud3ec\uc2a4\ud305\uc740 \uaf2d \uc791\uc131\ud558\uc790!!\\n\\n:::\\n\\n- [ ] **[\ud544\uc218]**\ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc744 \uc815\uc758 \ub0b4\ub9b4 \uc218 \uc788\ub294 \uac1c\ubc1c\uc790\ub4e4\uc740 \uc5bc\ub9c8\ub098 \ub420\uae4c?\\n\\n  - **[\ud544\uc218]**\ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc758 \uc815\uc758\\n  - **[\ud544\uc218]**\uc790\uc720\ub3c4\uac00 \uc81c\ud55c\ub41c \ucef4\ud3ec\ub10c\ud2b8 \uae30\ubc18 \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\\n  - **[\ud544\uc218]**\uc790\uc720\ub3c4\uac00 \ub192\uc740 \ud30c\uc6b4\ub370\uc774\uc158 \uae30\ubc18 \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\\n  - \ub514\uc790\uc778 \uc2dc\uc2a4\ud15c\uc774 \uc2e4\ud328\ud558\ub294 \uc774\uc720\ub294?\\n\\n- [ ] Headless UI \ub780?\\n\\n- [ ] Storybook Design System Guide \uc815\ub9ac\\n\\n- [ ] \ub9ac\uc561\ud2b8\uc5d0\uc11c\uc758 \uac00\ub3c5\uc131\uc774\ub780?\\n\\n  - \ube44\ub3d9\uae30 \ucc98\ub9ac \ubc29\uc2dd(Suspense)\\n  - useEffect \ub0a8\ubc1c\uc740 \ub3c5\uc774 \ub420\ub4ef.\\n  - \uc88b\uc740 react builtin hook\ub4e4\uc744 \uc54c\uc544\ubcf4\uace0 \uc0ac\uc6a9\ud574\ubcf4\uae30.\\n\\n- [ ] **[\ud544\uc218]**\ubaa8\ub358 \ud504\ub808\uc784\uc6cc\ud06c(React,Vue)\uc758 \ud55c\uacc4\uc640 \ud604\uc2e4"}]}')}}]);